mapper.select:
select pt.id as taskId, pt.project_id, pt.status, pt.task_name, pt.created_time, ps.stage_name from pmhub_project_task pt left join pmhub_project_stage ps on pt.project_stage_id = ps.id
where pt.deleted = 0 and pt.created_by = #{username}

    and pt.project_id =#{projectId}

order by pt.created_time desc

select pt.id as taskId, pt.project_id, pt.status, pt.task_name, pt.created_time, ps.stage_name from pmhub_project_task pt left join pmhub_project_stage ps on pt.project_stage_id = ps.id
where pt.deleted = 0 and pt.user_id = #{userId}

    and pt.project_id =#{projectId}

order by pt.created_time desc

select pt.id as taskId, pt.project_id, pt.status, pt.task_name, pt.created_time, ps.stage_name from pmhub_project_task pt
    inner join pmhub_project_member pm on pt.id = pm.pt_id
    left join pmhub_project_stage ps on pt.project_stage_id = ps.id
where pt.deleted = 0 and pm.type = 'task' and pm.user_id = #{userId}

    and pt.project_id =#{projectId}

order by pt.created_time desc

select ptp.approved, ptp.instance_id as procInsId, ptp.deployment_id as deployId, ptp.definition_id, ptp.task_id as taskProcessId,p.project_name, pt.task_process,pt.id as taskId, pt.task_name, ps.stage_name, su.nick_name as executor, pt.status, pt.execute_status, pt.task_priority, su2.nick_name as createdBy, pt.created_time,pt.close_time,pt.begin_time,pt.end_time from pmhub_project_task pt
left join pmhub_project p on p.id = pt.project_id
left join pmhub_project_member pm on pt.id = pm.pt_id
left join pmhub_project_stage ps on pt.project_stage_id = ps.id
left join sys_user su on su.user_id = pt.user_id
left join sys_user su2 on su2.user_name = pt.created_by
left join pmhub_project_task_process ptp on pt.id = ptp.extra_id
where pt.deleted = 0 and pm.type = 'task' and pm.user_id = #{userId}

    and pt.task_name = #{data.taskName}


    and pt.project_id = #{data.projectId}


    and ps.stage_code = #{data.stageCode}


    and su.nick_name = #{data.executor}


    and su2.nick_name = #{data.createdBy}


    and pt.execute_status = #{data.executeStatus}


    and pt.status = #{data.status}


    and pt.task_priority = #{data.taskPriority}

order by pt.created_time desc

select ptp.approved, ptp.instance_id as procInsId, ptp.deployment_id as deployId, ptp.definition_id, ptp.task_id as taskProcessId,pt.project_id,p.project_name,pt.task_process,pt.id as taskId, pt.task_name, ps.stage_name, su.nick_name as executor, pt.status, pt.execute_status, pt.task_priority, su2.nick_name as createdBy, pt.created_time,pt.close_time,pt.begin_time,pt.end_time
from pmhub_project_task pt
left join pmhub_project p on pt.project_id = p.id
left join pmhub_project_stage ps on pt.project_stage_id = ps.id
left join sys_user su on su.user_id = pt.user_id
left join sys_user su2 on su2.user_name = pt.created_by
left join pmhub_project_task_process ptp on pt.id = ptp.extra_id
where pt.deleted = 0 and pt.project_id = #{data.projectId}

    and pt.task_name = #{data.taskName}


    and ps.stage_code = #{data.stageCode}


    and su.nick_name = #{data.executor}


    and su2.nick_name = #{data.createdBy}


    and pt.execute_status = #{data.executeStatus}


    and pt.status = #{data.status}


    and pt.task_priority = #{data.taskPriority}

order by pt.created_time desc

select pt.task_name, p.project_name, ps.stage_name, su.nick_name as executor, pt.status, pt.execute_status, pt.task_priority, su2.nick_name as createdBy, pt.created_time from pmhub_project_task pt left join pmhub_project p on p.id = pt.project_id left join pmhub_project_stage ps on pt.project_stage_id = ps.id left join sys_user su on su.user_id = pt.user_id left join sys_user su2 on su2.user_name = pt.created_by where pt.deleted = 0
and pt.id in
#{taskId}

select * from pmhub_project p where p.deleted = 0

    and p.id in

        #{projectId}
